<script setup>
import { ref, reactive, watch } from "vue";
import {
  APIgetRecordList,
  APIpostRecord,
  APIputRecord,
  APIdeleteRecord,
  APIgetRecordDetail,
  APIgetKindList,
} from "@/api/custom/custom";
import { ElMessage } from "element-plus";
import { Search, Plus } from "@element-plus/icons-vue";
import { getFilesKeys } from "@/util/files.js";
const page = ref(1);
const per_page = ref(10);
const recordList = reactive({
  arr: [],
});
const find_right_typeId_to_name = (typeid) => {
  let name = "";
  recordKindList.arr.find((item, index) => {
    for (let key in item) {
      if (item[key] == typeid) {
        console.log(item.name);
        name = item["name"];
      }
    }
  });
  return name;
};
const switch_add_record = ref(false);
const refreshPage = () => {
  page.value = 1;
  per_page.value = 10;
  getRecordList();
  getRecordKindList();
};
const recordKindList = reactive({ arr: [] });
const getRecordKindList = () => {
  APIgetKindList("filing").then((res) => {
    recordKindList.arr = res;
    console.log(res);
  });
};

const from_record = reactive({
  item: {
    china_code: "",
    type: "",
    zone_id: "",
    group_id: "",
    title: "",
    sno: "",
    desc: "",
    affix: [
      {
        name: "",
        key: [],
        type: "",
      },
    ],
    startat: "",
    endat: "",
    status: 0,
  },
});
const add_put_title = ref("");
const addRecord = () => {
  switch_add_record.value = true;
  add_put_title.value = "添加";
};
const tree_item = ref({
    id: '50',
    name: '测试',
    next_type: 'region',
    type: 'region'
})
//备案列表详情
const getFunRecordList = () => {
  let data = {
    // page: data_1.page,
    // per_page: data_1.per_page
  };
  for (let key in data_1.search) {
    let item = data_1.search[key];
    if (item || item === 0) {
      data[key] = item;
    }
  }
  console.log(data);
  APIgetRecordList(data).then((res) => {
    console.log(res);
    data_1.total = res.length;
    data_1.list = res;
    recordList.arr = res;
  });
};
//关闭修改弹窗dialog事件
const record_dialog_close = () => {
  from_record.item = {
    china_code: "",
    type: "",
    zone_id: "",
    group_id: "",
    title: "",
    sno: "",
    desc: "",
    affix: [
      {
        name: "",
        key: [],
        type: "",
      },
    ],
    startat: "",
    endat: "",
    status: 0,
  };
};
//添加备案选择备案小区事件
const checkFunc=()=>{

}
// 文件上传
const fileListFn = (val) => {
  if (!val) {
    return [];
  }
  if (typeof val == "string") {
    return [
      {
        name: val,
      },
    ];
  }
  return [val];
};
const data_1 = reactive({
  search: {},
  switch_search: false,
  page: 1,
  total: 0,
  per_page: 15,
  add_switch: false,
});
//城市接口
import {
    APIgetChinaRegion
} from '@/api/custom/custom.js'
APIgetChinaRegion().then(res => {
    console.log(res)
    tree_item.value.id = res.data[0].code
    tree_item.value.name = res.data[0].name
    tree_item.value.next_type = 'region'
    tree_item.value.type = 'region'
})
//switch开关
const switchRecordFun = (status, val) => {
  let params = {};
  for (let key in val) {
    if (val[key] !== null) {
      console.log(key);
      params[key] = val[key];
    }
  }
  params.status = status;
  APIputRecord(val.id, params).then((res) => {
    if ((res.status = 200)) {
      ElMessage.success("修改成功");
    }
  });
  console.log(params);
};
const deleteRecord = (val) => {
  APIdeleteRecord(val).then((res) => {
    ElMessage.success("删除成功");
    refreshPage();
  });
};
//筛选重置按钮
const refreshFunc = () => {
  data_1.search = {};
  data_1.switch_search = false;
  data_1.page = 1;
  getFunRecordList();
};
//监听事件
watch(
  data_1.page,
  () => {
    refreshFunc();
  },
  { immediate: true, deep: true }
);
//得到备案列表
const getRecordList = () => {
  APIgetRecordList().then((res) => {
    console.log(res);
    recordList.arr = res;
  });
};
const putRecord = (val) => {
  switch_add_record.value = true;
  add_put_title.value = "修改";
  current_record.item = val;
  APIgetRecordDetail(val.id).then((res) => {
    console.log(res);
    from_record.item = res;
  });
};
const current_record = reactive({
  item: {},
});
// 添加确认按钮
const dialogExamineCloseFunc = () => {
  let obj = {};
  add_put_title.value == "添加";
  for (let i in from_record.item.affix) {
    if (typeof from_record.item.affix[i].key != "string") {
      obj[i] = from_record.item.affix[i].key;
    }
  }
  let files = [];
  for (let i in obj) {
    files.push(obj[i].raw);
  }
  if (files.length > 0) {
    getFilesKeys(files, "folder").then((arr) => {
      let o = 0;
      for (let i in obj) {
        from_record.item.affix[i].key = arr[o];
        o++;
      }
      submit_post_put();
    });
    return false;
  }
  submit_post_put();
};
const submit_post_put = () => {
  if (add_put_title.value == "修改") {
    APIputRecord(current_record.item.id, from_record.item).then((res) => {
      if (res.status == 200) {
        ElMessage.success("修改成功");
        switch_add_record.value = false;
        refreshPage();
      }
    });
  }
  if (add_put_title.value == "添加") {
    APIpostRecord(from_record.item).then((res) => {
      ElMessage.success("添加成功");
      switch_add_record.value = false;
      refreshPage();
    });
  }
};
